import React, { useState } from "react";
import axios from "axios";
import "./Post.css";
import img_pro from "../image/icon.png";

const CreatePost = ({ onPostCreated }) => {
  const [title, setTitle] = useState("");
  const [content, setContent] = useState("");

  const handleSubmit = async (event) => {
    event.preventDefault();
    const url = "http://127.0.0.1:8000/posts/create/1";
    const data = { title, content };
    const headers = {
      "Content-Type": "application/json",
      Authorization: `Bearer ${localStorage.getItem("access_token")}`,
    };
    try {
      const response = await axios.post(url, data, { headers });
      console.log(response.data);
      onPostCreated(response.data); // notify parent component of new post

      setTitle(""); // clear the title input
      setContent(""); // clear the content input
    } catch (error) {
      console.error(error);
    }
  };

  return (
    <section className="createpost ">
        <div className="contain">
        <div className="bg"></div>
      <div className="pro-img">
        <div className="bg-post"></div>
        <img src={img_pro} alt="" />
      </div>
      <form onSubmit={handleSubmit} className="Form">
        <div className="input">
          <label htmlFor="title">Title: </label>
          <input
            type="text"
            id="title"
            value={title}
            onChange={(event) => setTitle(event.target.value)}
          />
        </div>
        <div className="input">
          <label htmlFor="content">Content: </label>
          <textarea
            id="content"
            value={content}
            onChange={(event) => setContent(event.target.value)}
          ></textarea>
        </div>
        <button type="submit">Create post</button>
      </form>
        </div>
      
    </section>
  );
};

export default CreatePost;
