import React, { useState } from "react";
import axios from "axios";
import "./Comment.css";
import img_pro from "../image/icon.png";

const CreateComment = ({ onPostCreated }) => {
  const [content, setContent] = useState("");

  const handleSubmit = async (event) => {
    event.preventDefault();
    const url = "http://127.0.0.1:8000/comments/create/";
    const data = {content };
    const headers = {
      "Content-Type": "application/json",
      Authorization: `Bearer ${localStorage.getItem("access_token")}`,
    };
    try {
      const response = await axios.comment(url, data, { headers });
      console.log(response.data);
      onPostCreated(response.data); // notify parent component of new post

      setContent(""); // clear the content input
    } catch (error) {
      console.error(error);
    }
  };

  return (
    <section className="createpost ">
        <div className="contain">
      <form onSubmit={handleSubmit} className="Form">
        <div className="input">
          <label htmlFor="content">Content: </label>
          <textarea
            id="content"
            value={content}
            onChange={(event) => setContent(event.target.value)}
          ></textarea>
        </div>
        <button type="submit">Create comment</button>
      </form>
        </div>
      
    </section>
  );
};

export default CreateComment;
